CREATE TABLE `detalle_circulante` (
  `codigodetallecirculante` int(3) NOT NULL AUTO_INCREMENT,
  `codigo` int(15) NOT NULL,
  `descripcion` text COLLATE utf8_spanish_ci NOT NULL,
